const links=document.querySelector("#links"),hamburger=document.querySelector("#hamburger"),body=document.querySelector("body");let timeout=!1;delay=250,calls=0,hamburgerActive=!1,hamburger.addEventListener("click",handleClickHamburgerMenu);function handleClickHamburgerMenu(){hamburgerActive=!hamburgerActive,hamburgerActive?(links.classList.add("active"),hamburger.classList.add("open"),body.classList.add("open")):(links.classList.remove("active"),hamburger.classList.remove("open"),body.classList.remove("open"))}function checkMediaQuery(){window.innerWidth>=768&&(hamburgerActive=!1,links.classList.remove("active"),hamburger.classList.remove("open"))}var mql=window.matchMedia("(min-width: 768px)");mql.addEventListener("change",()=>{clearTimeout(timeout),timeout=setTimeout(checkMediaQuery,delay)})