const masonryLayout=(e,t,n)=>{e.classList.add("masonry-layout",`columns-${n}`);let s=[];for(let t=1;t<=n;t++){let o=document.createElement("ul");o.classList.add("masonry-column",`column-${t}`),e.appendChild(o),s.push(o)}for(let e=0;e<Math.ceil(t.length/n);e++)for(let o=0;o<n;o++){let i=t[e*n+o];i&&(s[o].appendChild(i),i.classList.add("masonry-item"))}},list=document.querySelector(".list"),listCard=document.querySelectorAll(".list-card");list&&listCard&&masonryLayout(list,listCard,2);const replies=document.querySelector(".replies"),repliesCard=document.querySelectorAll(".replies-card");replies&&repliesCard&&masonryLayout(replies,repliesCard,2)