window.addEventListener("DOMContentLoaded",()=>{const e=new IntersectionObserver(e=>{console.log(e),e.forEach(e=>{const t=e.target.getAttribute("id");e.isIntersecting?document.querySelector(`nav li a[href="#${t}"]`).parentElement.classList.add("active"):document.querySelector(`nav li a[href="#${t}"]`).parentElement.classList.remove("active")})});document.querySelectorAll("h1[id],h2[id],h3[id], h4[id]").forEach(t=>{e.observe(t)})})